DROP DATABASE IF EXISTS db_unesco;

CREATE DATABASE db_unesco;

USE db_unesco;

CREATE TABLE t_user (
    id_user SMALLINT(6) AUTO_INCREMENT,
    username VARCHAR(255),
    password VARCHAR(255),
    PRIMARY KEY (id_user)
);

CREATE TABLE t_site_unesco (
    id_site_unesco SMALLINT(6) AUTO_INCREMENT,
    states_name VARCHAR(255),
    region VARCHAR(255),
    iso_code VARCHAR(255),
    udnp_code VARCHAR(255),
    image VARCHAR(255),
    link VARCHAR(255),
    short_description VARCHAR(255),
    name VARCHAR(255),
    longitutude VARCHAR(255),
    latitude VARCHAR(255),
    category VARCHAR(255),
    PRIMARY KEY (id_site_unesco)
);

CREATE TABLE t_history (
    id_history SMALLINT(6) AUTO_INCREMENT,
    fk_user SMALLINT(6),
    fk_site_unesco SMALLINT(6),
    PRIMARY KEY (id_history),
    FOREIGN KEY (fk_user) REFERENCES t_user(id_user),
    FOREIGN KEY (fk_site_unesco) REFERENCES t_site_unesco(id_site_unesco)
);

